import { useDispatch, useSelector } from "react-redux";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

const CommitActivity = ({ repoName, owner }) => {
  const dispatch = useDispatch();
  const codeFrequency = useSelector((state) => state.codeFrequency);

  const [displayType, setDisplayType] = useState("both"); // Default display type is both

  useEffect(() => {
    const fetchCodeFrequency = async () => {
      const response = await fetch(
        `https://api.github.com/repos/${owner}/${repoName}/stats/code_frequency`
      );
      const myData = await response.json();
      dispatch({ type: "codeFrequency/fetchCodeFrequency", payload : myData });
    };

    fetchCodeFrequency();
  }, [dispatch, repoName, owner]);

  const handleDisplayTypeChange = (event) => {
    setDisplayType(event.target.value);
  };

  const contributorsData = codeFrequency?.map((week) => ({
    timestamp: week[0],
    contributors: week.slice(1),
  }));

  const uniqueContributors = Array.from(
    new Set(
      contributorsData?.flatMap((data) => data.contributors?.map((c) => c.author))
    )
  );

  const chartOptions = {
    chart: {
      type: "line",
    },
    title: {
      text: `Commit Activity for ${repoName}`,
    },
    xAxis: {
      categories: contributorsData?.map((data) => data.timestamp),
      title: {
        text: "Timestamp",
      },
    },
    yAxis: {
      title: {
        text: "Total Changes",
      },
    },
    tooltip: {
      formatter: function () {
        const contributorData =
          this.points[0].point.contributorData[this.series.name];
        return `Timestamp: ${this.x}<br/>Changes: ${this.y}<br/>Contributor: ${contributorData.author}`;
      },
      shared: true,
    },
    plotOptions: {
      series: {
        marker: {
          enabled: false,
        },
      },
    },
    series: uniqueContributors.map((contributor, index) => ({
      name: contributor,
      data: contributorsData.map((data) => {
        const contributorData = data.contributors.find(
          (c) => c.author === contributor
        ) || { total: 0 };
        return {
          y:
            displayType === "additions"
              ? contributorData.total.additions
              : contributorData.total.deletions,
          contributorData,
        };
      }),
      visible: true, // You can toggle visibility in the legend
    })),
  };

  return (
    <div>
      <h2>Commit Activity for {repoName}</h2>
      <label>
        Display Type:
        <select value={displayType} onChange={handleDisplayTypeChange}>
          <option value="additions">Additions</option>
          <option value="deletions">Deletions</option>
          <option value="both">Both</option>
        </select>
      </label>

    </div>
  );
};

export default CommitActivity;
