import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import { fetchCodeFrequencyData } from './CodeFrequencyService';



import axios from 'axios';

const BASE_URL = 'https://api.example.com'; // Replace with your API base URL

const apiService = axios.create({
  baseURL: BASE_URL,
});

export const fetchChartData = (owner, repo) => {
  // Replace with your actual API endpoint and parameters
  const endpoint = `/repos/${owner}/${repo}/stats/code_frequency`;

  return apiService.get(endpoint)
    .then((response) => response.data)
    .catch((error) => {
      console.error('Error fetching data:', error);
      throw error;
    });
};

const DynamicHighchart = ({ owner, repo }) => {
  const dispatch = useDispatch();
  const chartData = useSelector((state) => state.codeFrequency);

  useEffect(() => {
    dispatch(fetchCodeFrequencyData(owner, repo));
  }, [dispatch, owner, repo]);

  const chartOptions = {
    // Highcharts configuration options
    title: {
      text: `Dynamic Highchart for ${owner}/${repo}`,
    },
    // ... other Highcharts options
  };

  return (
    <HighchartsReact highcharts={Highcharts} options={chartOptions} />
  );
};

export default DynamicHighchart;
