import "./Repo.css"
import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchRepositories } from '../redux/repositoriesSlice';
import DropDown from'../components/DropDown'
import DynamicHighchart from "./DropDownChart";

const RepositoryList = () => {
  // const options = [
  //   { value: 'commits', label: 'commits' },
  //   { value: 'additions', label: 'additions' },
  //   { value: 'deletions', label: 'deletions' },
  // ];
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
 };
 const handleClick = (event) => {
  alert(`You clicked: ${event.target.textContent}`);
};
  const dispatch = useDispatch();
  const repositories = useSelector((state) => state.repositories.data);
  const page = useSelector((state) => state.repositories.page);

  const [timePeriod, setTimePeriod] = useState('1w');
  const [selectedRepo, setSelectedRepo] = useState(null);
  const bottomOfListRef = useRef(null);

  useEffect(() => {
    const date = new Date();
    let createdSince;

    switch (timePeriod) {
      case '1w':
        createdSince = new Date(date.setDate(date.getDate() - 7)).toISOString();
        break;
      case '2w':
        createdSince = new Date(date.setDate(date.getDate() - 14)).toISOString();
        break;
      case '1m':
        createdSince = new Date(date.setMonth(date.getMonth() - 1)).toISOString();
        break;
      default:
        createdSince = null;
    }

    if (createdSince) {
      dispatch(
        fetchRepositories({
          q: `created:>${createdSince}`,
          sort: 'stars',
          order: 'desc',
          per_page: 10,
        })
      );
    }
  }, [dispatch, timePeriod]);

  const handleScroll = () => {
    if (bottomOfListRef.current && bottomOfListRef.current.getBoundingClientRect().bottom <= window.innerHeight) {
      dispatch(
        fetchRepositories({
          q: `created:>${new Date().toISOString()}`,
          sort: 'stars',
          order: 'desc',
          per_page: 10,
          page: page + 1,
        })
      );
    }
  };


  return (
    <div>
      <form className="d-flex align-items-center flex-column mb-3">
      <label>
          Select Time Period:
          <select
            value={timePeriod}
            onChange={(e) => setTimePeriod(e.target.value)}
          >
            <option value="1w">1 Week</option>
            <option value="2w">2 Weeks</option>
            <option value="1m">1 Month</option>
          </select>
        </label>
        <button type="submit" className="mt-5">Search</button>
      </form>
      <div className="container-fluid mt-5">
        <h2>
          Top Repositories (Last{" "}
          {timePeriod === "1w"
            ? "Week"
            : timePeriod === "2w"
            ? "2 Weeks"
            : "Month"}
          )
        </h2>

        {repositories?.map((repo) => (
          <div key={repo.id.value}>


            <div className=" d-flex">

              <p className="border p-1 border-3">
                <img
                  src={repo.owner.avatar_url}
                  className="img_width col-md-12 p-2 "
                />
              </p>
              <div className="row row-cols-2">
                <p className="w-100 ms-5 mt-3">{repo.name}</p>
                <p className=" w-100 ms-5">{repo.description}</p>
                <p className="border border-3 count ms-5 align-self-center text-center">
                  {repo.stargazers_count} Stars
                </p>
                <p className="border border-3 count ms-5 align-self-center text-center">
                  {repo.open_issues} issues
                </p>

                <p className="text-end flex-column">Last Pushed {repo.pushed_at} by {repo.name}</p>

              </div>
              <button onClick={toggleDropdown}>Toggle Dropdown</button>
      {showDropdown && (
        <div>
          <button onClick={handleClick}>Option 1</button>
          <button onClick={handleClick}>Option 2</button>
          <button onClick={handleClick}>Option 3</button>
        </div>
      )}
              <hr />
              <DropDown />

            </div>
            <DynamicHighchart/>
          </div>
        ))}
      </div>

    </div>
  );
};

export default RepositoryList;
