import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchRepositories } from '../redux/repositoriesSlice';
import CommitActivity from './CommitActivity';

const RepositoryList = () => {
  const dispatch = useDispatch();
  const repositories = useSelector((state) => state.repositories.data);
  const page = useSelector((state) => state.repositories.page);

  const [timePeriod, setTimePeriod] = useState('1w');
  const [selectedRepo, setSelectedRepo] = useState(null);
  const bottomOfListRef = useRef(null);

  useEffect(() => {
    const date = new Date();
    let createdSince;

    switch (timePeriod) {
      case '1w':
        createdSince = new Date(date.setDate(date.getDate() - 7)).toISOString();
        break;
      case '2w':
        createdSince = new Date(date.setDate(date.getDate() - 14)).toISOString();
        break;
      case '1m':
        createdSince = new Date(date.setMonth(date.getMonth() - 1)).toISOString();
        break;
      default:
        createdSince = null;
    }

    if (createdSince) {
      dispatch(
        fetchRepositories({
          q: `created:>${createdSince}`,
          sort: 'stars',
          order: 'desc',
          per_page: 10,
        })
      );
    }
  }, [dispatch, timePeriod]);

  const handleScroll = () => {
    if (bottomOfListRef.current && bottomOfListRef.current.getBoundingClientRect().bottom <= window.innerHeight) {
      dispatch(
        fetchRepositories({
          q: `created:>${new Date().toISOString()}`,
          sort: 'stars',
          order: 'desc',
          per_page: 10,
          page: page + 1,
        })
      );
    }
  };

  const handleRepoClick = (repo) => {
    setSelectedRepo(repo);
  };

  return (
    <div>
      <form>
      <label>
          Select Time Period:
          <select
            value={timePeriod}
            onChange={(e) => setTimePeriod(e.target.value)}
          >
            <option value="1w">1 Week</option>
            <option value="2w">2 Weeks</option>
            <option value="1m">1 Month</option>
          </select>
        </label>
        <button type="submit">Search</button>
      </form>
      <h2>Top Repositories (Last {timePeriod === '1w' ? 'Week' : timePeriod === '2w' ? '2 Weeks' : 'Month'})</h2>
      <ul>
        {repositories.map((repo, index) => (
          <li key={repo.id}>
            <strong onClick={() => handleRepoClick(repo)} style={{ cursor: 'pointer', color: 'blue' }}>
              {repo.name}
            </strong>
            <p>{repo.description}</p>
            <p>{repo.stargazers_count} Stars</p>
            <a href={repo.html_url} target="_blank" rel="noopener noreferrer">
              View on GitHub
            </a>
            {index === repositories.length - 1 && <div ref={bottomOfListRef}></div>}
            <hr />
          </li>
        ))}
      </ul>
      {selectedRepo && (
        <CommitActivity repoName={selectedRepo.name} owner={selectedRepo.owner.login} />
      )}
    </div>
  );
};

export default RepositoryList;
