import { useState } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";

const GITHUB_API_URL = 'https://api.github.com/repos/octocat/hello-world/stats/code_frequency'

const Chart = ({ chartRef, setChartInfo }) => {
    const [data, setData] = useState(null);

    async function fetchData() {
        const response = await fetch('https://my-api.com/endpoint');
        const json = await response.json();
        setData(json);
      }
  const [options] = useState({
    plotOptions: {
      series: {
        point: {

        }
      }
    },
    series: [
      {
        data: [2, 7, 5, 1, 4]
      },
      {
        data: [4, 3, 5, 6, 2, 3]
      }
    ]
  });

  return (
    <>
    {data === null && <p>Loading data...</p>}
      {data !== null && (
        <div>
          <p>Data has been loaded!</p>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
       <HighchartsReact ref={chartRef} highcharts={Highcharts} options={options} />
    </>

  );
};

export default Chart;
