import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import "./Repo.css"
const Chart = ({ chartRef, setChartInfo },{repoName, owner}) => {
      const [options] = useState({
        plotOptions: {
          series: {
            point: {

            }
          }
        },
        series: [
          {
            data: [2, 7, 5, 1, 4]
          },
          {
            data: [4, 3, 5, 6, 2, 3]
          }
        ]
      });

  const dispatch = useDispatch();
  const codeFrequency = useSelector((state) => state.codeFrequency);

  const [displayType, setDisplayType] = useState('commits'); // Default display type is both

  useEffect(() => {
    const fetchCodeFrequency = async () => {
      const response = await fetch(`https://api.github.com/repos/octocat/hello-world/stats/code_frequency`);
      const data = await response.json();
      dispatch({ type: 'codeFrequency/fetchCodeFrequency', payload: data });
    };

    fetchCodeFrequency();
  }, [dispatch, repoName, owner]);

  const handleDisplayTypeChange = (event) => {
    setDisplayType(event.target.value);
  };

  const contributorsData = codeFrequency && codeFrequency.map && codeFrequency.map((week) => ({
    timestamp: week[0],
    contributors: week.slice(1),
  }));

  const uniqueContributors = Array.from(new Set(contributorsData?.flatMap((data) => data.contributors.map((c) => c.author))));

  const chartOptions = {
    chart: {
      type: 'line',
    },
    title: {
      text: `Commit Activity for ${repoName}`,
    },
    xAxis: {
      categories: contributorsData?.map((data) => data.timestamp),
      title: {
        text: 'Timestamp',
      },
    },
    yAxis: {
      title: {
        text: 'Total Changes',
      },
    },
    tooltip: {
      formatter: function () {
        const contributorData = this.points[0].point.contributorData[this.series.name];
        return `Timestamp: ${this.x}<br/>Changes: ${this.y}<br/>Contributor: ${contributorData.author}`;
      },
      shared: true,
    },
    plotOptions: {
      series: {
        marker: {
          enabled: false,
        },
      },
    },
    series: uniqueContributors?.map((contributor, index) => ({
      name: contributor,
      data: contributorsData.map((data) => {
        const contributorData = data.contributors.find((c) => c.author === contributor) || { total: 0 };
        return {
          y: displayType === 'additions' ? contributorData.total.additions : contributorData.total.deletions,
          contributorData,
        };
      }),
      visible: true,
    })),
  };

  return (
    <div>
      <h2>{repoName}</h2>
      <label className='mt-4'>

        <select value={displayType} onChange={handleDisplayTypeChange} className='btn btn-light btn-sm h-25 '>
          <option value="commits">Commits</option>
          <option value="additions">Additions</option>
          <option value="deletions">Deletions</option>
        </select>
      </label>


    </div>
  );
};

export default Chart;

