// codeFrequencySlice.js

import { createSlice } from '@reduxjs/toolkit';
import { fetchChartData } from './apiService';

const codeFrequencySlice = createSlice({
  name: 'codeFrequency',
  initialState: {
    data: [],
    loading: false,
    error: null,
  },
  reducers: {
    fetchCodeFrequencyStart: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchCodeFrequencySuccess: (state, action) => {
      state.loading = false;
      state.data = action.payload;
    },
    fetchCodeFrequencyFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

export const {
  fetchCodeFrequencyStart,
  fetchCodeFrequencySuccess,
  fetchCodeFrequencyFailure,
} = codeFrequencySlice.actions;

export default codeFrequencySlice.reducer;

