import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import { fetchChartData } from './UserService';
import { setChartData } from '../redux/chartdataSlice';

const DynamicHighchart = () => {
  const dispatch = useDispatch();
  const selectedOption = useSelector((state) => state.dropdown.selectedOption);
  const chartData = useSelector((state) => state.chartData.data);

  useEffect(() => {
    if (selectedOption) {
      fetchChartData(selectedOption.value)
        .then((data) => {
          dispatch(setChartData(data)); // Dispatch the actual action to update chart data
        })
        .catch((error) => {
          console.error('Error fetching chart data:', error);
        });
    }
  }, [dispatch, selectedOption]);

  const chartOptions = {
    // Highcharts configuration options
    title: {
      text: `Dynamic Highchart for ${selectedOption?.label || 'No Option Selected'}`,
    },
    // ... other Highcharts options
  };

  return (
    <HighchartsReact highcharts={Highcharts} options={chartOptions} />
  );
};

export default DynamicHighchart;
