// UserDropdownHighcharts.js

import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Select from 'react-select';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import { fetchUsers } from './UserService';
import { selectUser } from '../redux/userSlice';

const UserDropdownHighcharts = () => {
  const dispatch = useDispatch();

  const [users, setUser] = useState([{
    title : "",
    description : ""
  }]);
  const selectedUser = useSelector((state) => state.users.selectedUser);

  useEffect(() => {
    // Fetch users when the component mounts
    fetchUsers();
  }, []);

  const handleUserChange = (selectedOption) => {
    const selectedUserId = selectedOption.value;
    const selectedUserData = users.find((user) => user.id === selectedUserId);
    dispatch(selectUser(selectedUserData));
  };

  const chartOptions = {
    // Highcharts configuration options
    title: {
      text: `Commit Activity for ${selectedUser ? selectedUser.name : ''}`,
    },
    // ... other Highcharts options
  };

  return (
    <div>
      <label>Select User:</label>
      <Select
        options={users.map((user) => ({ value: user.id, label: user.name }))}
        onChange={handleUserChange}
        value={selectedUser ? { value: selectedUser.id, label: selectedUser.name } : null}
      />
      {selectedUser && (
        <div>
          <h2>Selected User</h2>
          <p>Name: {setUser.name}</p>
          <p>Email: {setUser.email}</p>
          {/* Add more user details as needed */}
        </div>
      )}
      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
    </div>
  );
};

export default UserDropdownHighcharts;
