import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';

const CommitActivity = ({ repoName, owner }) => {
  const dispatch = useDispatch();
  const commitActivity = useSelector((state) => state.commitActivity);

  const [displayType, setDisplayType] = useState('both'); // Default display type is both

  useEffect(() => {
    // Fetch commit activity data for the last year
    // You may need to adjust the API endpoint and parameters based on the GitHub API
    const fetchCommitActivity = async () => {
      const response = await fetch(`https://api.github.com/repos/${owner}/${repoName}/hello-world/stats/code_frequency`);
      const data = await response.json();
      dispatch({ type: 'commitActivity/fetchCommitActivity', payload: data });
    };

    fetchCommitActivity();
  }, [dispatch, repoName, owner]);

  const handleDisplayTypeChange = (event) => {
    setDisplayType(event.target.value);
  };

  useEffect(() => {
    const chartData = commitActivity?.map((week) =>
      displayType === 'additions'
        ? week.additions
        : displayType === 'deletions'
        ? week.deletions
        : week.additions + week.deletions
    );

    const chartOptions = {
      chart: {
        type: 'line',
      },
      title: {
        text: `Commit Activity for ${repoName}`,
      },
      xAxis: {
        categories: commitActivity?.map((_, index) => `Week ${index + 1}`),
        title: {
          text: 'Weeks',
        },
      },
      yAxis: {
        title: {
          text: 'Total Changes',
        },
      },
      tooltip: {
        formatter: function () {
          return `Week ${this.x + 1}<br/>Changes: ${this.y}<br/>Timestamp: ${this.point.timestamp}`;
        },
      },
      series: [
        {
          name: 'Total Changes',
          data: chartData,
        },
      ],
    };

    Highcharts.chart('commitChart', chartOptions);
  }, [commitActivity, displayType, repoName]);

  return (
    <div>
      <h2>Commit Activity for {repoName}</h2>
      <label>
        Display Type:
        <select value={displayType} onChange={handleDisplayTypeChange}>
          <option value="additions">Additions</option>
          <option value="deletions">Deletions</option>
          <option value="both">Both</option>
        </select>
      </label>
      <div id="commitChart"></div>
    </div>
  );
};

export default CommitActivity;
