import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import { fetchCommitData } from './commitdataThunks';

const CommitChart = ({ owner, repo }) => {
  const dispatch = useDispatch();
  const commitData = useSelector((state) => state.commitData.data);

  useEffect(() => {
    dispatch(fetchCommitData(owner, repo));
  }, [dispatch, owner, repo]);

  const getSeries = () => {
    // Process commitData to create series for Highcharts
    // Example: Extract contributor names and weekly changes
    const contributors = Array.from(new Set(commitData.map((commit) => commit.author)));
    return contributors.map((contributor) => {
      const contributorCommits = commitData.filter((commit) => commit.author === contributor);
      const weeklyChanges = contributorCommits.map((commit) => commit.changes);
      return {
        name: contributor,
        data: weeklyChanges,
      };
    });
  };

  const chartOptions = {
    chart: {
      type: 'line',
    },
    title: {
      text: 'Contributors\' Weekly Changes',
    },
    xAxis: {
      // Your X-axis configuration
      // For human-readable week start dates, you may need to format your dates accordingly
    },
    yAxis: {
      title: {
        text: 'Total Changes',
      },
    },
    tooltip: {
      // Tooltip configuration
      formatter: function () {
        return `${this.series.name}<br/>Week: ${this.x}<br/>Changes: ${this.y}`;
      },
    },
    legend: {
      // Legend configuration
      layout: 'vertical',
      align: 'right',
      verticalAlign: 'middle',
    },
    series: getSeries(),
  };

  return <HighchartsReact highcharts={Highcharts} options={chartOptions} />;
};

export default CommitChart;
