// filterSlice.js
import { createSlice } from '@reduxjs/toolkit';

const filterSlice = createSlice({
  name: 'filter',
  initialState: {
    showAdditions: true,
  },
  reducers: {
    toggleShowAdditions: (state) => {
      state.showAdditions = !state.showAdditions;
    },
  },
});

export const { toggleShowAdditions } = filterSlice.actions;
export const selectShowAdditions = (state) => state.filter.showAdditions;
export default filterSlice.reducer;
