import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchRepositories } from '../redux/repositoriesSlice';

const RepositoryList = () => {
  const dispatch = useDispatch();
  const repositories = useSelector((state) => state.repositories.data);

  const [timePeriod, setTimePeriod] = useState('1w');

  const handleSubmit = (e) => {
    e.preventDefault();

    const date = new Date();
    let createdSince;

    switch (timePeriod) {
      case '1w':
        createdSince = new Date(date.setDate(date.getDate() - 7)).toISOString();
        break;
      case '2w':
        createdSince = new Date(date.setDate(date.getDate() - 14)).toISOString();
        break;
      case '1m':
        createdSince = new Date(date.setMonth(date.getMonth() - 1)).toISOString();
        break;
      default:
        createdSince = null;
    }

    if (createdSince) {
      dispatch(
        fetchRepositories({
          q: `created:>${createdSince}`,
          sort: 'stars',
          order: 'desc',
        })
      );
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Select Time Period:
          <select value={timePeriod} onChange={(e) => setTimePeriod(e.target.value)}>
            <option value="1w">1 Week</option>
            <option value="2w">2 Weeks</option>
            <option value="1m">1 Month</option>
          </select>
        </label>
        <button type="submit">Search</button>
      </form>
      <h2>Top Repositories (Last {timePeriod === '1w' ? 'Week' : timePeriod === '2w' ? '2 Weeks' : 'Month'})</h2>
      {repositories.map((repo) => (
        <div key={repo.id}>
          <p>{repo.name}</p>
          <p>{repo.stargazers_count} Stars</p>
          <p>{repo.html_url}</p>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default RepositoryList;
