// chartDataThunks.js

import { fetchChartDataStart, fetchChartDataSuccess, fetchChartDataFailure } from './chartDataSlice';

export const fetchChartData = (owner, repo) => async (dispatch) => {
  try {
    dispatch(fetchChartDataStart());

    // Use native fetch API
    const response = await fetch(`https://api.example.com/repos/${owner}/${repo}/stats/code_frequency`);
    const data = await response.json();

    dispatch(fetchChartDataSuccess(data));
  } catch (error) {
    dispatch(fetchChartDataFailure(error.message));
  }
};
