import React, { useState, useEffect } from 'react'
export default function Posts() {
  const [api, setapi] = useState([])
  const API = 'https://api.github.com/repos/octocat/hello-world/stats/code_frequency';
  const fetchPost = () => {
    fetch(API)
      .then((res) => res.json())
      .then((res) => {
        console.log(res)
        setapi(res)
      })
  }
  useEffect(() => {
    fetchPost()
  }, [])
  return (
    <>
      <h2>React Fetch Data with REST API Example</h2>
      <ul>
        {api.map((item, i) => {
          return {item}
        })}
      </ul>
    </>
  )
}
