import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchRepositories } from '../redux/repositoriesSlice';

const RepositoryList = ({ timeFrame }) => {
  const dispatch = useDispatch();
  const repositories = useSelector((state) => state.repositories.data);

  useEffect(() => {
    const date = new Date();
    let createdSince;

    switch (timeFrame) {
      case '1w':
        createdSince = new Date(date.setDate(date.getDate() - 7)).toISOString();
        break;
      case '2w':
        createdSince = new Date(date.setDate(date.getDate() - 14)).toISOString();
        break;
      case '1m':
        createdSince = new Date(date.setMonth(date.getMonth() - 1)).toISOString();
        break;
      default:
        createdSince = null;
    }

    if (createdSince) {
      dispatch(
        fetchRepositories({
          q: `created:>${createdSince}`,
          sort: 'stars',
          order: 'desc',
        })
      );
    }
  }, [dispatch, timeFrame]);

  return (
    <div>
      <h2>Top Repositories (Last {timeFrame === '1w' ? 'Week' : timeFrame === '2w' ? '2 Weeks' : 'Month'})</h2>
      {repositories.map((repo) => (
        <div key={repo.id}>
          <p>{repo.name}</p>
          <p>{repo.stargazers_count} Stars</p>
          <p>{repo.html_url}</p>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default RepositoryList;
