// DynamicHighchart.js

import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

import { setChartData } from '../redux/chartdataSlice';

const DynamicHighchart = () => {
    // Sample chart data structure
const chartData = [
    { category: 'Week 1', value1: 10, value2: 20 },
    { category: 'Week 2', value1: 15, value2: 25 },
    // ... more data
  ];

  const dispatch = useDispatch();
  const selectedOption = useSelector((state) => state.dropdown.selectedOption);


  useEffect(() => {
    if (selectedOption) {
      fetchChartData(selectedOption.value)
        .then((data) => {
          dispatch(setChartData(data)); // Dispatch the actual action to update chart data
        })
        .catch((error) => {
          console.error('Error fetching chart data:', error);
        });
    }
  }, [dispatch, selectedOption]);

  const chartOptions = {
    chart: {
      type: 'line', // Specify the chart type (line, column, bar, etc.)
    },
    title: {
      text: `Dynamic Highchart for ${selectedOption?.label || 'No Option Selected'}`,
    },
    xAxis: {
      categories: chartData.map((item) => item.category), // Replace with your actual data property
      title: {
        text: 'X-Axis Label',
      },
    },
    yAxis: {
      title: {
        text: 'Y-Axis Label',
      },
    },
    series: [
      {
        name: 'Series 1',
        data: chartData.map((item) => item.value1), // Replace with your actual data property
      },
      {
        name: 'Series 2',
        data: chartData.map((item) => item.value2), // Replace with your actual data property
      },
    ],
    // Add more configuration options as needed
  };

  return <HighchartsReact highcharts={Highcharts} options={chartOptions} />;
};

export default DynamicHighchart;
